mapper.select:
SELECT * FROM userinfo LEFT JOIN department ON userinfo.department_id = department.id WHERE delete_date IS NULL
AND userinfo.id = #{id}
AND name LIKE CONCAT('%', #{name}, '%')
AND department_id = #{departmentId}
AND department_name LIKE CONCAT('%', #{departmentName}, '%')
ORDER BY userinfo.id ${sortOrder}

SELECT * FROM userinfo LEFT JOIN department ON userinfo.department_id = department.id WHERE delete_date IS NOT NULL
AND userinfo.id = #{id}
AND name LIKE CONCAT('%', #{name}, '%')
AND department_id = #{departmentId}
AND department_name LIKE CONCAT('%', #{departmentName}, '%')
ORDER BY userinfo.id ${sortOrder}
